import React from 'react';
import { Container , Row, Col} from 'react-bootstrap';

import classes from './Hero.module.scss';

const Hero = props => {
  return (
    <Container>
      <Row>
        <Col>
          <h1 className='text-center text-light pt-5'>Gradie</h1>
          <p className='text-center'>Beautiful gradients in seconds.</p>
          <p className={classes.sub-header}>Beautiful gradients in seconds.</p>
        </Col>
      </Row>
    </Container>
  );
};

export default Hero;
