  return (
    <div className={classes.hero}>
        <h1 className='pt-5'>Gradie</h1>
        <p className={classes.sub_header}>Beautiful gradients in seconds.</p>
        <div className={classes.img_container}>
          {/* <Image src={phone} fluid></Image> */}
        </div>

    </div>

  );
};

export default Hero;
